	@RestController
	public class UserController {

		@Autowired
		private UserService userService;	// get one time instance

		@RequestMapping("/users")			// default method = SET
		public ArrayList<User> getUserList(){
			ArrayList<User> userList = userService.getUserList();
			return userList;
		}
	}
